package 제어문;

public class 반복문3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int j = 0; j<5; j++) {
		for (int i = 0; i < 10; i++) {
			  //시작값;   조건문;   증감연산자
			System.out.print("*"); // 별 한줄에 for문 하나
	     }
		System.out.println(""); // 줄 바꿈
		}

	}

}
