package 데이터;

public class 기본데이터2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // 기본 데이터 4가지: 정수,실수,문자,논리
		// 기호 => 연산자
		int age = 28; //정수
		double height =122.2; //실수
 		char gender ='남'; //문자형 1개
		boolean food = true; //false 논리
		
		System.out.println("내 나이는: " +age); //결합 연산자
		//글자들 : 문자 + 문자 + 문자+....(여러글자,문자열,String(기본데이터 x),실)
		System.out.println("내 몸무게는: "+height);
		System.out.println(gender);
		System.out.println(food);
		
	}

}
